import React from 'react'
import logo from '../../img/puma-logo.svg';
import bg1 from '../../img/img-1.jpg';
import bg2 from '../../img/img-2.jpg';
import bg3 from '../../img/img-3.jpg';
import bg4 from '../../img/img-4.jpg';
import bg5 from '../../img/img-5.jpg';
import Menu from './../Menu/Menu';
// import Card from './../Card/Card';
import personFirst from '../../img/person-1.jpg';
import personSecond from '../../img/person-2.jpg';
import personThird from '../../img/person-3.jpg';
import personFourth from '../../img/person-4.jpg';
import personFiveth from '../../img/person-5.jpg';
import './App.sass';

const menu = [
    {
        text: 'Studio'
    },
    {
        text: 'Partners'
    },
    {
        text: 'Media'
    },
    {
        text: 'user'
    }
]

const card = {
	img: [personFirst, personSecond, personThird, personFourth, personFiveth],
	alt: "profileImg",
	profileName: ["Carl Yohan", "May Clarcson", "Zac Manson", "Nelson Onil", "Adam Burns"]
}

const App = () => {

	const [point, setPoint] = React.useState({
		strokeDashoffset: 1004,
		background: bg1
	});

	let goStep = (strokeLength, srcBg) => {
		setTimeout(() => {
			setPoint({
			strokeDashoffset: strokeLength,
			background: srcBg
		})}) 
		// setRadiusLength(1004);
		// setBackground(bg1);
	}

	// let secondStep = () => {
	// 	setRadiusLength(753);
	// 	setBackground(bg2);
	// }

	// let thirdStep = () => {
	// 	setRadiusLength(502);
	// 	setBackground(bg3);
	// }

	// let fourthStep = () => {
	// 	setRadiusLength(251);
	// 	setBackground(bg4);
	// }

	// let fivesthStep = () => {
	// 	setRadiusLength(0);
	// 	setBackground(bg5);
	// }

		return (
			<div id="main"
				style={{ backgroundImage: `url(${point.background})` }}
			>
				<div className="navbar">
						<div className="logo">
							<img src={logo} className="App-logo" alt="logo" />
						</div>
						<nav>
							<ul>
								<Menu items={menu} />
							</ul>
						</nav>
				</div>
				<div className="circle-box">
					<svg height="402px" width="402px">
						<circle r="200" cx="201" cy="201" strokeWidth="1px" stroke="grey" fill="none" />
					</svg>
					<svg height="402px" width="402px">
						<circle r="200" cx="201" cy="201" strokeWidth="1px" stroke="#fff" fill="none" strokeDasharray="1256" strokeDashoffset={point.strokeDashoffset} id="svgCircle" />
					</svg>
					<div id="steps">
						<span id="step1" onClick={() => goStep(1004, bg1)}></span>
						<span id="step2" onClick={() => goStep(753, bg2)}></span>
						<span id="step3" onClick={() => goStep(502, bg3)}></span>
						<span id="step4" onClick={() => goStep(251, bg4)}></span>
						<span id="step5" onClick={() => goStep(0, bg5)}></span>
					</div>
					<div className="cards">
						{/* <Card person={} person-alt={} person-name={} person-status={} about-persone={} /> */}
						<div className="card">
							<div className="img-profile">
								<img src="" alt="img-profile"/>
							</div>
							<div className="profile-name">
								<span></span>
							</div>
							<div className="profile-status">
								<span></span>
							</div>
							<div className="about-profile">
								<span></span>
							</div>
						</div>
						<div className="card">
							<div className="img-profile">
								<img src="" alt="img-profile"/>
							</div>
							<div className="profile-name">
								<span></span>
							</div>
							<div className="profile-status">
								<span></span>
							</div>
							<div className="about-profile">
								<span></span>
							</div>
						</div>
						<div className="card">
							<div className="img-profile">
								<img src="" alt="img-profile"/>
							</div>
							<div className="profile-name">
								<span></span>
							</div>
							<div className="profile-status">
								<span></span>
							</div>
							<div className="about-profile">
								<span></span>
							</div>
						</div>
						<div className="card">
							<div className="img-profile">
								<img src="" alt="img-profile"/>
							</div>
							<div className="profile-name">
								<span></span>
							</div>
							<div className="profile-status">
								<span></span>
							</div>
							<div className="about-profile">
								<span></span>
							</div>
						</div>
						<div className="card">
							<div className="img-profile">
								<img src="" alt="img-profile"/>
							</div>
							<div className="profile-name">
								<span></span>
							</div>
							<div className="profile-status">
								<span></span>
							</div>
							<div className="about-profile">
								<span></span>
							</div>
						</div>
					</div>
				</div>
			</div>
		)
	}

export default App;
