import React, { Component } from 'react'
import logo from '../../img/puma-logo.svg';
import bg1 from '../../img/img-1.jpg';
import bg2 from '../../img/img-2.jpg';
import bg3 from '../../img/img-3.jpg';
import bg4 from '../../img/img-4.jpg';
import bg5 from '../../img/img-5.jpg';
import Menu from './../Menu/Menu';
import './App.sass';

const menu = [
    {
        text: 'Studio'
    },
    {
        text: 'Partners'
    },
    {
        text: 'Media'
    },
    {
        text: 'user'
    }
]

export class App extends Component {
	constructor(props) {
		super(props);
		this.firstStep = this.firstStep.bind(this);
		this.secondStep = this.secondStep.bind(this);
		this.thirdStep = this.thirdStep.bind(this);
		this.fourthStep = this.fourthStep.bind(this);
		this.fivesthStep = this.fivesthStep.bind(this);
		this.state = {radiusLength: 1004, background: bg1};
	}

	firstStep() {
		this.setState({radiusLength: 1004, background: bg1});
	}

	secondStep() {
		this.setState({radiusLength: 753});
		this.setState({background: bg2});
	}

	thirdStep() {
		this.setState({radiusLength: 502});
		this.setState({background: bg3});
	}

	fourthStep() {
		this.setState({radiusLength: 251});
		this.setState({background: bg4});
	}

	fivesthStep() {
		this.setState({radiusLength: 0});
		this.setState({background: bg5});
	}

	render() {

		let radiusLength = this.state.radiusLength;
		let background = this.state.background;

		// switch (radiusLength) {
		// 	case 1004:

		// 		break;
		// 	case 753:
		// 		background = bg3;
		// 		break;
		// 	case 502:
		// 		background = bg4;
		// 		break;
		// 	case 251:
		// 		background = bg5;
		// 		break;
		// 	case 0:
		// 		background = bg1;
		// 		break;
		// 	default:
		// 		break;
		// }

		return (
			<div id="main"
				style={{ backgroundImage: `url(${background})` }}
			>
				<div className="navbar">
						<div className="logo">
							<img src={logo} className="App-logo" alt="logo" />
						</div>
						<nav>
							<ul>
								<Menu items={menu} />
							</ul>
						</nav>
				</div>
				<div className="circle-box">
					<svg height="402px" width="402px">
						<circle r="200" cx="201" cy="201" strokeWidth="1px" stroke="grey" fill="none" />
					</svg>
					<svg height="402px" width="402px">
						<circle r="200" cx="201" cy="201" strokeWidth="1px" stroke="#fff" fill="none" strokeDasharray="1256" strokeDashoffset={radiusLength} id="svgCircle" />
					</svg>
					<div>
						<span id="step1" onClick={this.firstStep}></span>
						<span id="step2" onClick={this.secondStep}></span>
						<span id="step3" onClick={this.thirdStep}></span>
						<span id="step4" onClick={this.fourthStep}></span>
						<span id="step5" onClick={this.fivesthStep}></span>
					</div>
				</div>
			</div>
		)
	}
}

export default App;
