import React, { Component } from 'react'
import logo from '../../img/puma-logo.svg';
import Background from '../../img/img-1.jpg';
import Menu from './../Menu/Menu';
import './App.sass';

const menu = [
    {
        text: 'Studio'
    },
    {
        text: 'Partners'
    },
    {
        text: 'Media'
    },
    {
        text: 'user'
    }
]

export class App extends Component {
	constructor(props) {
		super(props);
		this.firstStep = this.firstStep.bind(this);
		this.secondStep = this.secondStep.bind(this);
		this.thirdStep = this.thirdStep.bind(this);
		this.fourthStep = this.fourthStep.bind(this);
		this.fivesthStep = this.fivesthStep.bind(this);
		this.state = {radiusLength: 256};
	}

	firstStep() {
		this.setState({radiusLength: -256});
	}

	secondStep() {
		this.setState({radiusLength: -512});
	}

	thirdStep() {
		this.setState({radiusLength: -768});
	}

	fourthStep() {
		this.setState({radiusLength: -1024});
	}

	fivesthStep() {
		this.setState({radiusLength: -1256});
	}

	render() {

		let radiusLength = this.state.radiusLength;

		return (
			<div id="main"
				style={{ backgroundImage: `url(${Background})` }}
			>
				<div className="navbar">
						<div className="logo">
							<img src={logo} className="App-logo" alt="logo" />
						</div>
						<nav>
							<ul>
								<Menu items={menu} />
							</ul>
						</nav>
				</div>
				<div className="circle-box">
					<svg height="402px" width="402px">
						<circle r="200" cx="201" cy="201" strokeWidth="1px" stroke="grey" fill="none" />
					</svg>
					<svg height="402px" width="402px">
						<circle r="200" cx="201" cy="201" strokeWidth="1px" stroke="#fff" fill="none" strokeDasharray="1256" strokeDashoffset={radiusLength} id="svgCircle" />
					</svg>
					<div>
						<span id="step1" onClick={this.firstStep}></span>
						<span id="step2" onClick={this.firstStep}></span>
						<span id="step3" onClick={this.firstStep}></span>
						<span id="step4" onClick={this.firstStep}></span>
						<span id="step5" onClick={this.firstStep}></span>
					</div>
				</div>
			</div>
		)
	}
}

export default App;
