import React, { Component } from 'react'
import logo from '../../img/puma-logo.svg';
import bg1 from '../../img/img-1.jpg';
import bg2 from '../../img/img-2.jpg';
import bg3 from '../../img/img-3.jpg';
import bg4 from '../../img/img-4.jpg';
import bg5 from '../../img/img-5.jpg';
import Menu from './../Menu/Menu';
import './App.sass';

const menu = [
    {
        text: 'Studio'
    },
    {
        text: 'Partners'
    },
    {
        text: 'Media'
    },
    {
        text: 'user'
    }
]

App = () => {

	state = {radiusLength: 1004, background: bg1};


	firstStep = () => {
		setState({radiusLength: 1004, background: bg1});
	}

	secondStep = () => {
		setState({radiusLength: 753, background: bg2});
	}

	thirdStep = () => {
		setState({radiusLength: 502, background: bg3});
	}

	fourthStep = () => {
		setState({radiusLength: 251, background: bg4});
	}

	fivesthStep = () => {
		setState({radiusLength: 0, background: bg5});
	}

		let radiusLength = state.radiusLength;
		let background = state.background;

		return (
			<div id="main"
				style={{ backgroundImage: `url(${background})` }}
			>
				<div className="navbar">
						<div className="logo">
							<img src={logo} className="App-logo" alt="logo" />
						</div>
						<nav>
							<ul>
								<Menu items={menu} />
							</ul>
						</nav>
				</div>
				<div className="circle-box">
					<svg height="402px" width="402px">
						<circle r="200" cx="201" cy="201" strokeWidth="1px" stroke="grey" fill="none" />
					</svg>
					<svg height="402px" width="402px">
						<circle r="200" cx="201" cy="201" strokeWidth="1px" stroke="#fff" fill="none" strokeDasharray="1256" strokeDashoffset={radiusLength} id="svgCircle" />
					</svg>
					<div>
						<span id="step1" onClick={() => firstStep()}></span>
						<span id="step2" onClick={() => secondStep()}></span>
						<span id="step3" onClick={() => thirdStep()}></span>
						<span id="step4" onClick={() => fourthStep()}></span>
						<span id="step5" onClick={() => fivesthStep()}></span>
					</div>
				</div>
			</div>
		)
	}

export default App;
